let header=document.getElementById("header-sticky"),progress=document.getElementById("progress-bar");function setStickyHeader(){let e=document.getElementById("post-header"),t=document.getElementById("post-image"),n=document.getElementById("contents").getBoundingClientRect().height;if(null!==e&&null!==t){let o=0;o+=e.getBoundingClientRect().height,o+=t.getBoundingClientRect().height,window.pageYOffset>e.getBoundingClientRect().height+64?(header.style.top="0px",header.style.opacity="1"):(header.style.top="-68px",header.style.opacity="0");let d=n+o-document.documentElement.clientHeight+64,l=(document.body.scrollTop||document.documentElement.scrollTop)/d*100;progress.style.width=l+"%"}}window.onload=function(){setStickyHeader()},window.onscroll=function(){setStickyHeader()};